import React, { PureComponent } from 'react';
import { Provider } from 'react-redux';
import { createStore } from 'redux';

const reducer = (state, action) => {
	const { type, payload, payload2 } = action;
	switch(type) {
		case 'SET_LOADING': {
			return {
				...state,
				loading: payload,
				name: payload2,
			};
		}
		case 'RESET_LOADING': {
			return {
				...state,
				loading: false
			};
		}
		case 'SET_USER': {
			return {
				...state,
				user: payload,
			};
		}
		default:
			return state;
	}
};

class ReduxApp extends PureComponent {
	store = createStore(
		reducer,
		{ loading: false, name: '두잇 리액트' },
		window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__(),
	);

	componentDidMount(){
		this.store.dispatch({
			type: 'SET_LOADING',
			payload: true,
			payload2: 'sksksksksksksk',
		});
		this.store.dispatch({
			type: 'RESET_LOADING'
		});
		this.store.dispatch({
			type: 'SET_USER',
			payload: { name: 'Park', age: 30 },
		});
	}

	render() {
		return (
			<Provider store={this.store}>
				리덕스 예제
			</Provider>
		);
	}
}

export default ReduxApp;
